<template>  <view class="foot-menu">    <view class="foot-menu__item" :class="{ active: parseInt(checked) === index }"          v-for='(item, index) in menus' :key="item" @click="goToPage(index)">      <view class="foot-menu__icon" :style="{backgroundPositionX: (-index*60 + 1).toString() + 'rpx' }"></view>      <view class="foot-menu__title">{{ item }}</view>    </view>  </view></template><script>export default {  name: "footMenu",  props: {    checked: {      type: String,      default: 0    }  },  data() {    return {      menus: ["首页", "小编推荐", "9.9包邮", "优惠线报", "分类"],      pages: ["/", "/pages/recommend/recommend", "/pages/99/99", "/pages/infocenter/infocenter", "/pages/category/category"]    }  },  methods: {    goToPage(index) {      uni.navigateTo({        url: this.pages[index]      })    }  }}</script><style scoped lang="scss">.foot-menu {  height: 93rpx;  padding-top: 8rpx;  background-color: #ffffff;  display: flex;  position: fixed;  z-index: 1001;  bottom: 0;&__item {   width: 150rpx;   height: 100%;   text-align: center; }&__title {   font-size: 24rpx;   color: rgb(51, 51, 51); }&__icon {   width: 60rpx;   height: 60rpx;   margin: 0 auto;   background-image:url('../../static/foot_menu_bgc.png');   background-repeat: no-repeat;   background-size: 300rpx 120rpx;   background-position-x: -1rpx;   background-position-y: 0; }}.active {.foot-menu__title {  color: rgba(242, 16, 31, 0.77);}.foot-menu__icon {  background-position-y: -60rpx;}}</style>