<template>  <view class="recommend-goods">    // #ifdef H5    <div ref="imgContainerDom">      // #endif      <view class="recommend-goods-item" v-for="recommendGoods in data" :key="recommendGoods.item_id" @click="goToGoodsPage(recommendGoods)">        // #ifdef H5        <img src="http://cmsstatic.ffquan.cn//web/images/rolling.gif" :data-src="recommendGoods.pict_url" alt="" class="recommend-goods-item__img">        // #endif        // #ifndef H5        <image mode="" :src="recommendGoods.pict_url" lazy-load="true" class="recommend-goods-item__img"></image>        // #endif        <view class="recommend-goods-item__title">          <image mode="widthFix" src="data:image/png;base64,"></image>          {{ recommendGoods.title }}        </view>        <view class="recommend-goods-item__price">          券后&nbsp;          <view class="recommend-goods-item__price__red">            ¥            <text>{{ (recommendGoods.zk_final_price - recommendGoods.coupon_amount).toFixed(2) }}</text>          </view>        </view>        <view class="recommend-goods-item__coupon">          <text>券</text>          {{ recommendGoods.coupon_amount }} 元        </view>        <view class="recommend-goods-item__volume">          已售{{ recommendGoods.volume }}        </view>      </view>      // #ifdef H5    </div>    // #endif  </view></template><script>import { lazyLodImg } from "../../static/common";import { ref } from "vue";import { goToGoodsPage } from "../../static/common";export default {  name: "goodsItem",  props: {    data: {      type: Array,      default: []    }  },  setup() {    const imgContainerDom = ref(null)    // #ifdef H5    lazyLodImg(imgContainerDom)    // #endif    return {      imgContainerDom,      goToGoodsPage    }  }}</script><style scoped lang="scss">.recommend-goods {  padding: 20rpx;  border-radius: 12px;  padding-top: 0;  padding-bottom: 108rpx;  background-color: #f4f4f4;  .recommend-goods-item {    width: calc(50% - 20rpx);    //box-sizing: border-box;    margin: 0 10rpx 20rpx 10rpx;    display: inline-block;    background-color: #ffffff;    &__img {      width: 100%;      height: calc(50vw - 40rpx);    }    &__title {      :last-child {        width: 26rpx;        height: 26rpx;      }      padding: 10rpx 20rpx;      font-size: 26rpx;      font-weight: 400;      color: #333333;      overflow: hidden;      text-overflow: ellipsis;      white-space: nowrap;    }    &__price {      padding-left: 20rpx;      color: rgb(102, 102, 102);      font-size: 24rpx;      display: flex;      align-items: baseline;      &__red {        color: #ff2b22;        font-size: 24rpx;        font-weight: bold;        :last-child {          font-size: 38rpx;        }      }    }    &__coupon {      background-image: linear-gradient(90deg,#ff8873,#ff4f4f);      font-size: 24rpx;      margin-left: 20rpx;      display: inline-block;      border-radius: 2px;      padding: 2rpx 6rpx 2rpx 2rpx;      color: #ffffff;      :first-child {        background-color: #ffffff;        color: #fe3a33;        padding: 0 2rpx;      }    }    &__volume {      font-size: 24rpx;      color: #888;      padding: 10rpx 20rpx 20rpx 20rpx;    }  }}</style>